.panel {
  padding: 0.4em;
  position: relative;
  display: grid;
}
.panel::before,
.panel::after {
  content: "";
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  border-radius: 4px;
  z-index: -1;
  pointer-events: none;
}
.panel::before {
  background: var(--vscode-foreground);
  opacity: 0.07;
}
.panel::after {
  border: 1px solid transparent;
}
.panel.active::after {
  border-color: var(--vscode-foreground);
  opacity: 0.7;
}
.panel.shadow::before {
  background: var(--vscode-foreground);
  opacity: 0.04;
}
.panel.top-stacked::before,
.panel.top-stacked::after {
  border-bottom-right-radius: 0;
  border-bottom-left-radius: 0;
}
.panel.bottom-stacked::before,
.panel.bottom-stacked::after {
  border-top-right-radius: 0;
  border-top-left-radius: 0;
}
.panel label {
  display: block;
  font-size: 0.8em;
  margin-left: 0.1em;
  margin-top: 0.3em;
  margin-bottom: 0.3em;
  opacity: 0.8;
}
.panel label:not(:first-child) {
  margin-top: 0.8em;
}
.record-editor {
  border-left: 2px solid transparent;
  padding-right: var(--i18n-ally-margin);
  padding-left: calc(var(--i18n-ally-margin) - 2px);
}
.record-editor.active {
  border-left: 2px solid var(--vscode-foreground);
}
.record-editor .edit-input {
  display: grid;
  grid-template-columns: max-content auto max-content max-content;
  margin-top: 8px;
}
.record-editor .edit-input .flag-icon {
  width: 2em;
  height: 1.8em;
  padding: 0.2em 0.2em 0.2em 0;
}
.record-editor .edit-input .buttons {
  margin: auto;
}
.record-editor .review-panel {
  padding-bottom: 8px;
}
.record-editor .comment-form {
  padding: 6px 12px;
  margin-top: 0.3em;
}
.record-editor .comment-form .buttons {
  margin-top: 0.7em;
  margin-bottom: 0.3em;
}
.record-editor .state-icon {
  padding-left: 0.2em;
  font-size: 1.1em;
  margin-top: -0.1em;
}
.record-editor .state-icon.earth-icon {
  opacity: 0.3;
}
.record-editor .state-icon.plus-minus-icon {
  color: var(--review-request-change);
}
.record-editor .state-icon.check-icon {
  color: var(--review-approve);
}
.record-editor .state-icon.comment-question-outline-icon {
  color: var(--review-comment);
}
.record-editor .state-icon.comment-outline-icon {
  opacity: 0.3;
}
.record-editor .state-icon.format-quote-open-icon {
  opacity: 0.6;
}
.record-editor .review-brief .state-icon {
  font-size: 1.4em;
